mapper.update:
UPDATE tbl_board
SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

UPDATE tbl_board 
SET title = #{title},
    content = #{content},
    writer = #{writer}
WHERE bno = #{bno}

UPDATE tbl_board 
SET likecnt = likecnt + #{amount} 
WHERE bno = #{bno}

UPDATE tbl_board 
SET dislikecnt = dislikecnt + #{amount} 
WHERE bno = #{bno}
